var generateIV = require('../help/generate_iv');
var base64url = require('../help/base64url');
module.exports = function (JWA, JWK) {
  ['A128GCMKW', 'A192GCMKW', 'A256GCMKW'].forEach(function (jwaAlg) {
    var encAlg = jwaAlg.substr(0, 7);
    var size = parseInt(jwaAlg.substr(1, 3), 10);
    var encrypt = JWA.encrypt.get(encAlg);
    var decrypt = JWA.decrypt.get(encAlg);
    if (encrypt && decrypt) {
      JWA.keyManagementEncrypt.set(jwaAlg, function (key, payload) {
        var iv = generateIV(jwaAlg);
        var _encrypt = encrypt(key, payload, {
            iv: iv
          }),
          ciphertext = _encrypt.ciphertext,
          tag = _encrypt.tag;
        return {
          wrapped: ciphertext,
          header: {
            tag: base64url.encodeBuffer(tag),
            iv: base64url.encodeBuffer(iv)
          }
        };
      });
      JWA.keyManagementDecrypt.set(jwaAlg, decrypt);
      JWK.oct.wrapKey[jwaAlg] = JWK.oct.unwrapKey[jwaAlg] = function (key) {
        return (key.use === 'enc' || key.use === undefined) && key.length === size;
      };
    }
  });
};
