/**
 * Given a callback, return a wrapped callback that will only run once regardless of calls.
 */
function once(callback) {
  var called = false;
  var value;
  return function () {
    if (!called) {
      value = callback.apply(void 0, arguments);
    }
    called = true;
    return value;
  };
}
module.exports = {
  once: once
};
