  const imgContainer = [
    { image: img1, comments: [] },
    { image: img2, comments: [] },
    { image: img3, comments: [] }
  ];
  const [enlargedImage, setEnlargedImage] = useState(null);

  const handleImageClick = (image, index) => {
    console.log("Image clicked:", image);
