      setEnlargedImage(null);
    } else {
      setEnlargedImage(image);
    }
  };

  const handleEnlargedImageClick = () => {
    setEnlargedImage(null);
  };

  return (
    <div>
      <Link to="/">
        <button className="galleryHomeBtn btn btn-primary">Go To Home</button>
      </Link>
      {imgContainer.map((item, index) => (
        <div key={index}> {/* Unique key for the image container */}
          <img
            id='galleryImg'
            src={item.image}
            alt={`Image ${index + 1}`}
            onClick={() => handleImageClick(item.image, index)}
            className={enlargedImage === item.image ? 'enlarged' : ''}
          />
          {enlargedImage === item.image && (
            <div className="enlarged-container">
              {/* Render WedComments for the enlarged image */}
              <WedComments comments={item.comments} />
            </div>
          )}
