import React from 'react'
// import husband from '../assets/breakdownImgA.jpg';
// import wife from '../assets/ashAshin.jpg';
// import mc from '../assets/derekG.jpg';
// import partyPlanner from '../assets/partyPlanner.jpg';
import bestMan from '../assets/bestMan.jpg';
import groomsMen from '../assets/groomsMen.jpg';
import groomsMan from '../assets/groomsMan.jpg';
import lauren from '../assets/lauren.jpg';
import nia from '../assets/Nia.jpg';
import marie from '../assets/Marie.jpg';
import alise from '../assets/Alise.jpg';
import Autumn from '../assets/Autumn.jpg';

import Navlinks from './Navlinks/Navlinks.jsx'
import "./weddingParty/weddingPartyStyling.css";

const WeddingParty = () => {

  const [enlargedImage, setEnlargedImage] = useState(null);
  const [selectedImageId, setSelectedImageId] = useState(null);

  const handleImageClick = (image, index) => {
    console.log("Image clicked:", image.imageId);
    setSelectedImageId(image.imageId)
    if (enlargedImage === image.imageId) {
      setEnlargedImage(null);
    } else {
      setEnlargedImage(image.imageId);
    }
  };

  const handleEnlargedImageClick = () => {
    setEnlargedImage(null);
  };

  return (
<div id='weddingPartyPage'>
  <Navlinks/>
  <div className="wedTeamContainer">
  <h3 id="wedTeamTitle">The Wedding Party</h3>
    <div className="wedTopSlider">
      {/* <img src={wife} alt="Ashley" className="sliderImg" /> */}
      <img onClick={() => handleImageClick} src={Autumn} alt="Autumn" className="sliderImg" />
      <img onClick={() => handleImageClick} src={bestMan} alt="Chris" className="sliderImg" />
      <img onClick={() => handleImageClick} src={nia} alt="Nia" className="sliderImg" />
      <img onClick={() => handleImageClick} src={lauren} alt="Lauren" className="sliderImg" />
      <img onClick={() => handleImageClick} src={groomsMan} alt="MikeJ" className="sliderImg" />
      <img onClick={() => handleImageClick} src={marie} alt="Marie" className="sliderImg" />
      <img onClick={() => handleImageClick} src={groomsMen} alt="MikeW" className="sliderImg" />
      <img onClick={() => handleImageClick} src={alise} alt="Alise" className="sliderImg" />
    </div>

    <div className="stylingBox">
      <p className="stylingBoxText">
        #BozierEverAfter
      </p>
    </div>
  </div>

</div>
  )
}

export default WeddingParty
